import { useState } from 'react'
const axios = require('axios')

const Contact = () => {

  const [senderName, setSenderName] = useState('')
  const [senderEmail, setSenderEmail] = useState('')
  const [message, setMessage] = useState('')

  const sendEmail = (e) => {
    e.preventDefault()

    axios.post('/api/send', {
      data: {
        personalizations:
          [
            {
              to: [
                {
                  email: "travis.v.williams@gmail.com",
                  name: "Travis Williams"
                }
              ],
              // subject: "Test"
            }
          ],
        content:
          [
            {
              type: "text/plain",
              value: message
            }
          ],
        from:
        {
          email: "travis.v.williams@gmail.com",
          name: "Travis Williams"
        },
        reply_to:
        {
          email: senderEmail,
          name: senderName
        }
      },
      headers: {
        Authorization: 'Bearer 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',
        'Content-type': 'application/json'
      }
    })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  }


  return (
    <div className='container' style={{ marginTop: '25px', textAlign: 'center' }}>
      <h1>Contact</h1>
      <form onSubmit={sendEmail} className="contact-form" name="contact" >
        <div className="form-group">
          <label>Name</label>
          <input onChange={(e) => {
            setSenderName(e.target.value)
          }} required type="text" className="form-control" placeholder="Name" name="name" id="yourname" />
        </div>
        <div className="form-group">
          <label>Email address</label>
          <input onChange={(e) => {
            setSenderEmail(e.target.value)
          }} required type="email" className="form-control" placeholder="name@example.com" name="email" id="youremail" />
        </div>
        <div className="form-group">
          <label>Message</label>
          <textarea onChange={(e) => {
            setMessage(e.target.value)
          }} required className="form-control" name="message" id="yourmessage" rows="3"></textarea>
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  )
}

export default Contact
