const axios = require('axios')

export default (req, res) => {
  const { data } = req.body

  axios.post('https://api.sendgrid.com/v3/mail/send', {
    data
  })
    .then(res => console.log(res))
    .catch(err => console.log(err))

  res.send('it worked')
}
